INSERT INTO `smt_tours` VALUES (2,'Tour Điện Biên Phủ tháng 12','Dien Bien\'s Tour','Điện Biên Phủ đã đi vào tiềm thức không những của người dân Việt Nam mà với tất cả những ai yêu chuộng hoà bình trên Thế Giới. Trấn động địa cầu đã trở thành câu nói bất hủ nơi đây mỗi khi nhắc đến Điện Biên',NULL,1,'2014-11-17 12:17:23',1,'2014-12-27 15:37:12','/Uploads/Administrator/Culture/Culture_Him%20Lam%202_Go%20trong.jpg','www.test.org'),(4,'Tour Du Lịch Tây Bắc 4 Ngày 3 Đêm','Tay Bac\'s Tour','Du lịch tây bắc - Điện Biên Phủ đã đi vào tiềm thức không những của người dân Việt Nam mà với tất cả những ai yêu chuộng hoà bình trên Thế Giới. Trấn động địa cầu đã trở thành câu nói bất hủ nơi đây mỗi khi nhắc đến Điện Biên',NULL,24,'2014-12-11 18:58:54',1,'2014-12-27 15:37:23','/Uploads/Administrator/Monument/khu%20du%20lich%20uva.jpg',NULL);
